INSERT INTO `angebot` (`angebotid`, `bezeichnung`, `beschreibung`, `bild`, `angebotspreis`, `fk_flug`) VALUES ('2', 'nach Düsseldorf', 'Düsseldorf ist Regierungssitz des bevölkerungsreichsten deutschen Bundeslandes Nordrhein-Westfalen. Die Rheinmetropole liegt mitten im Herzen der Wirtschaftsregion Rhein-Ruhr. Es gibt viel zu endecken.', NULL, NULL, NULL);
INSERT INTO `angebot` (`angebotid`, `bezeichnung`, `beschreibung`, `bild`, `angebotspreis`, `fk_flug`) VALUES ('3', 'nach Rom', 'Rom ist die grösste Stadt Italiens mit aktuell etwa 2,8 Mio. Einwohnern. Rom liegt im Herzen Italiens direkt am Tiber. Die Altstadt Roms, die Vatikanstadt und der Petersdom wurden von der UNESCO zum Weltkulturerbe erklärt.', NULL, NULL, NULL);
INSERT INTO `angebot` (`angebotid`, `bezeichnung`, `beschreibung`, `bild`, `angebotspreis`, `fk_flug`) VALUES ('4', 'nach Paris', 'Paris ist ein Abenteuer- an jeder Ecke atmet man die jahrhundertealte Geschichte der Stadt und kann sich in den unzähligen Straßen in der Betrachtung der traumhaft schönen Fassaden stundenlang verlieren.', NULL, NULL, NULL);
INSERT INTO `angebot` (`angebotid`, `bezeichnung`, `beschreibung`, `bild`, `angebotspreis`, `fk_flug`) VALUES ('5', 'nach Dublin', 'wenn wir uns die Insel im Atlantik als grünes Paradies vorstellen, dann ist die Hauptstadt im Osten vielleicht der Ort, wo die Paradiesfrucht hängt. Die wirtschaftliche Versuchung ist dort genauso zu Hause wie die Moderne und das pulsierende Leben.', NULL, NULL, NULL);
INSERT INTO `angebot` (`angebotid`, `bezeichnung`, `beschreibung`, `bild`, `angebotspreis`, `fk_flug`) VALUES ('6', 'nach Sydney', Sydney ist unzweifelhaft eine der schönsten Städte der Welt mit einer phantastischen Lage an einem weit ausladenen Naturhafenbecken. Sie wurde von der Natur verwöhnt: z.B. durch kilometerlange weiße Sandstrände direkt vor der Haustüre.', NULL, NULL, NULL);
INSERT INTO `angebot` (`angebotid`, `bezeichnung`, `beschreibung`, `bild`, `angebotspreis`, `fk_flug`) VALUES ('7', 'nach Side', 'Side liegt auf einer Halbinsel an der Mittelmeerküste und die Gebiete um Side herum sich mit vielen Hotels und Unterkünften touristisch erschlossen. Side ist ein sehr beliebten Ausflugsziel mit vielen Restaurants, Bars und Textil- bzw. Souveniergeschäften entwickelt.', NULL, NULL, NULL);
INSERT INTO `angebot` (`angebotid`, `bezeichnung`, `beschreibung`, `bild`, `angebotspreis`, `fk_flug`) VALUES ('8', 'nach Barcelona', 'Direkt am Mittelmeer gelegen bietet die Barcelona eine perfekte Kombination von Städtereise und Urlaub am Meer. Die Arbeiten des katalanischen Architekten Antonio Gaudí zählen zum Weltkulturerbe.', NULL, NULL, NULL);
INSERT INTO `angebot` (`angebotid`, `bezeichnung`, `beschreibung`, `bild`, `angebotspreis`, `fk_flug`) VALUES ('9', 'nach Bali', 'Berühmt ist dieses kleine Eiland wegen seiner einzigartigen Kultur und seiner liebenswerten Bevölkerung. Die Balinesen sind noch tief verwurzelt in ihrer Religion.', NULL, NULL, NULL);
INSERT INTO `angebot` (`angebotid`, `bezeichnung`, `beschreibung`, `bild`, `angebotspreis`, `fk_flug`) VALUES ('10', 'nach Miami', 'Miami ist an der Atlantikküste, an der Südspitze von Florida, gelegen. Es ist dort das ganze Jahr über warm und üblicherweise in den Sommermonaten heiß und feucht.', NULL, NULL, NULL);
INSERT INTO `angebot` (`angebotid`, `bezeichnung`, `beschreibung`, `bild`, `angebotspreis`, `fk_flug`) VALUES ('11', 'nach Lissabon', 'Die muntere Lebhaftigkeit dieser Stadt am Meer und deren faszinierende Schönheiten verlocken zu ausgedehnten Streifzügen. Verborgene Reize und der zärtliche Charme verleiten zum stillen Genießen, zu Ruhe und Erholung.', NULL, NULL, NULL);
INSERT INTO `angebot` (`angebotid`, `bezeichnung`, `beschreibung`, `bild`, `angebotspreis`, `fk_flug`) VALUES ('12', 'nach Hamburg', 'Hamburg, Deutschlands bedeutendste Hafenstadt, ist auch ein Magnet für Touristen.Mit dem größten Überseehafen des Landes verbindet sich automatisch die Vorstellung, in eine Stadt am Meer zu kommen.', NULL, NULL, NULL);
